import React, { useEffect, useState } from 'react';
import { firestore } from '../Database Connection/firebase';
import { collectionGroup, getDocs } from 'firebase/firestore';

function HomeScreenManager({ username }) {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        console.log('Fetching all team tasks');
        const querySnapshot = await getDocs(collectionGroup(firestore, 'user_tasks'));
        const tasksList = querySnapshot.docs.map(doc => ({ ...doc.data(), user: doc.ref.parent.parent.id }));
        setTasks(tasksList);
      } catch (error) {
        console.error('Error fetching tasks:', error);
        setError('Error fetching tasks. Please try again later.');
      }
    };
    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para MANAGER</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tareas del Equipo:</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {tasks.map((task, index) => (
        <div key={index}>
          <h3>Usuario: {task.user}</h3>
          <ul>
            <li>{task.description} - Due on {task.due_date} - {task.priority} Priority</li>
          </ul>
        </div>
      ))}
    </div>
  );
}

export default HomeScreenManager;
