// homeScreen.js

import React, { useEffect, useState } from 'react';
import { auth, firestore } from '../firebase';
import { collection, getDocs, query, where } from 'firebase/firestore';

const HomeScreen = () => {
  const [tasks, setTasks] = useState([]);
  const user = auth.currentUser;

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const q = query(collection(firestore, 'tasks', user.email, 'user_tasks'));
        const querySnapshot = await getDocs(q);
        const fetchedTasks = querySnapshot.docs.map(doc => doc.data());
        setTasks(fetchedTasks);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [user]);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {user.email.split('@')[0]}!</p>
      <h2>Tus Tareas:</h2>
      {tasks.length === 0 ? (
        <p>No tienes tareas.</p>
      ) : (
        tasks.map((task, index) => (
          <div key={index}>
            <h3>{task.name}</h3>
            <p>Detalles: {task.description}</p>
            <p>{task.due_date} - {task.priority} Priority</p>
          </div>
        ))
      )}
    </div>
  );
};

export default HomeScreen;
