// homeScreenManager.js

import React, { useEffect, useState } from 'react';
import { auth, firestore } from '../firebase';
import { collection, getDocs, query, where } from 'firebase/firestore';

const HomeScreenManager = () => {
  const [allTasks, setAllTasks] = useState([]);
  const user = auth.currentUser;

  useEffect(() => {
    const fetchAllTasks = async () => {
      try {
        const usersQuerySnapshot = await getDocs(collection(firestore, 'tasks'));
        const tasks = [];
        for (const userDoc of usersQuerySnapshot.docs) {
          const userId = userDoc.id;
          const userTasksQuerySnapshot = await getDocs(collection(firestore, 'tasks', userId, 'user_tasks'));
          userTasksQuerySnapshot.forEach(taskDoc => {
            tasks.push({ user: userId, ...taskDoc.data() });
          });
        }
        setAllTasks(tasks);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchAllTasks();
  }, [user]);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para MANAGER</h1>
      <p>¡Has iniciado sesión correctamente, {user.email.split('@')[0]}!</p>
      <h2>Tareas de Todos los Usuarios:</h2>
      {allTasks.length === 0 ? (
        <p>No hay tareas disponibles.</p>
      ) : (
        allTasks.map((task, index) => (
          <div key={index}>
            <h3>USUARIO: {task.user.split('@')[0]}</h3>
            <p>{task.user}</p>
            <p>{task.name}</p>
            <p>Detalles: {task.description}</p>
            <p>{task.due_date} - {task.priority} Priority</p>
          </div>
        ))
      )}
    </div>
  );
};

export default HomeScreenManager;
