// homeScreen.js
import React, { useEffect, useState } from 'react';
import { firestore } from '../Database Connection/firebase';
import { collection, query, where, getDocs } from 'firebase/firestore';

function HomeScreen({ username }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const userId = auth.currentUser.uid; // Usar el UID del usuario autenticado
      console.log("Fetching tasks for user_id:", userId);
      const tasksQuery = query(collection(firestore, 'tasks', userId, 'user_tasks'));
      const querySnapshot = await getDocs(tasksQuery);
      const userTasks = [];
      querySnapshot.forEach((doc) => {
        userTasks.push(doc.data());
      });
      console.log("Fetched tasks:", userTasks);
      setTasks(userTasks);
    };

    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tus Tareas:</h2>
      {tasks.length > 0 ? (
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>{task.description} - Due on {task.due_date} - {task.priority} Priority</li>
          ))}
        </ul>
      ) : (
        <p>No tienes tareas.</p>
      )}
    </div>
  );
}

export default HomeScreen;
