import React, { useEffect, useState } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { firestore, auth } from '../firebase';

function HomeScreenManager({ username }) {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAllTasks = async () => {
      try {
        const user = auth.currentUser;
        if (user) {
          const tasksRef = collection(firestore, 'tasks');
          const tasksSnapshot = await getDocs(tasksRef);
          let allTasks = [];

          for (let userDoc of tasksSnapshot.docs) {
            const userEmail = userDoc.id;
            const userTasksRef = collection(firestore, 'tasks', userEmail, 'user_tasks');
            const userTasksSnapshot = await getDocs(userTasksRef);
            const userTasks = userTasksSnapshot.docs.map(doc => ({
              ...doc.data(),
              email: userEmail
            }));
            allTasks = [...allTasks, ...userTasks];
          }

          setTasks(allTasks);
        }
      } catch (error) {
        console.error("Error fetching tasks:", error);
        setError('Error fetching tasks.');
      }
    };

    fetchAllTasks();
  }, []);

  const groupTasksByUser = () => {
    const groupedTasks = tasks.reduce((acc, task) => {
      if (!acc[task.email]) {
        acc[task.email] = [];
      }
      acc[task.email].push(task);
      return acc;
    }, {});

    return groupedTasks;
  };

  const groupedTasks = groupTasksByUser();

  return (
    <div>
      <h1>Bienvenido a la página de inicio para MANAGER</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tareas de Todos los Usuarios:</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {Object.keys(groupedTasks).length > 0 ? (
        Object.keys(groupedTasks).map((userEmail, index) => (
          <div key={index}>
            <h3>USUARIO: {userEmail.split('@')[0]}</h3>
            <p>Correo: {userEmail}</p>
            <ul>
              {groupedTasks[userEmail].map((task, i) => (
                <li key={i}>
                  {task.description} - Due on {task.due_date} - {task.priority} Priority
                </li>
              ))}
            </ul>
          </div>
        ))
      ) : (
        <p>No hay tareas disponibles.</p>
      )}
    </div>
  );
}

export default HomeScreenManager;
