import React, { useEffect, useState } from 'react';
import { firestore } from '../Database Connection/firebase';
import { collection, query, where, getDocs } from 'firebase/firestore';

function HomeScreen({ username }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const q = query(collection(firestore, 'tasks'), where('correo', '==', `${username}@gmail.com`));
      const querySnapshot = await getDocs(q);
      const tasksList = querySnapshot.docs.map(doc => doc.data());
      setTasks(tasksList);
    };
    fetchTasks();
  }, [username]);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tus Tareas:</h2>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>{task.description} - Due on {task.due_date} - {task.priority} Priority</li>
        ))}
      </ul>
    </div>
  );
}

export default HomeScreen;
