import React, { useEffect, useState } from 'react';
import { firestore } from '../Database Connection/firebase';
import { collection, getDocs } from 'firebase/firestore';

function HomeScreen({ username }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const tasksRef = collection(firestore, 'tasks', '6905325688', 'user_tasks');
      const querySnapshot = await getDocs(tasksRef);
      const fetchedTasks = querySnapshot.docs.map(doc => doc.data());
      console.log('Fetched tasks:', fetchedTasks);
      setTasks(fetchedTasks);
    };

    fetchTasks().catch(error => console.error('Error fetching tasks:', error));
  }, []);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tus Tareas:</h2>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            {task.description} - Due on {task.due_date} - {task.priority} Priority
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HomeScreen;
