import React, { useEffect, useState } from 'react';
import { firestore, auth } from '../firebase';
import { collection, query, where, getDocs } from 'firebase/firestore';

function HomeScreen({ username }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const user = auth.currentUser;
        if (user) {
          const q = query(collection(firestore, 'tasks', user.uid, 'user_tasks'));
          const querySnapshot = await getDocs(q);
          const tasksData = querySnapshot.docs.map((doc) => doc.data());
          setTasks(tasksData);
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tus Tareas:</h2>
      {tasks.length === 0 ? (
        <p>No tienes tareas.</p>
      ) : (
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              {task.description} - Due on {task.due_date} - {task.priority} Priority
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default HomeScreen;
