import React, { useEffect, useState } from 'react';
import { firestore } from '../Database Connection/firebase';
import { collection, getDocs, query, where } from 'firebase/firestore';

function HomeScreen({ username, userId }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      console.log(`Fetching tasks for user_id: ${userId}`);
      const q = query(collection(firestore, 'tasks', userId, 'user_tasks'));
      const querySnapshot = await getDocs(q);

      const userTasks = [];
      querySnapshot.forEach((doc) => {
        userTasks.push({ id: doc.id, ...doc.data() });
      });
      setTasks(userTasks);
      console.log('Fetched tasks:', userTasks);
    };

    if (userId) {
      fetchTasks();
    }
  }, [userId]);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tus Tareas:</h2>
      {tasks.length > 0 ? (
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              {task.description} - Due on {task.due_date} - {task.priority} Priority
            </li>
          ))}
        </ul>
      ) : (
        <p>No tienes tareas.</p>
      )}
    </div>
  );
}

export default HomeScreen;
