import React, { useState, useEffect } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { auth, firestore } from '../firebase'; // Asegúrate de que la ruta sea correcta

function HomeScreen({ username }) {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const user = auth.currentUser;
        if (!user) {
          setError('User not authenticated');
          return;
        }

        const userId = user.email;
        const tasksSnapshot = await getDocs(collection(firestore, 'tasks', userId, 'user_tasks'));
        const tasksData = tasksSnapshot.docs.map(doc => doc.data());
        setTasks(tasksData);
      } catch (error) {
        console.error('Error fetching tasks:', error);
        setError('Error fetching tasks');
      }
    };

    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Bienvenido a la página de inicio para USUARIO</h1>
      <p>¡Has iniciado sesión correctamente, {username}!</p>
      <h2>Tus Tareas:</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {tasks.length === 0 ? (
        <p>No tienes tareas.</p>
      ) : (
        tasks.map((task, index) => (
          <div key={index}>
            <h3>{task.description}</h3>
            <p>{task.due_date} - {task.priority} Priority</p>
            <p>Detalles: {task.details}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default HomeScreen;
